import React, { useState } from "react";
import "./navBar.css";
import logo from "../../assets/logo.png";
import { Link } from "react-scroll";
import menu from "../../assets/menu.png";

const NavBar = () => {
  const [show, setShow] = useState(false);

  const navItems = [
    { text: "Home", to: "introduction" },
    { text: "About", to: "about" },
    { text: "Projects", to: "projects" },
    { text: "Skills", to: "skills" },
    { text: "Education", to: "education" },
  ];

  const toggleMenu = () => {
    setShow(!show);
  };

  return (
    <nav className="navBar">
      <img src={logo} alt="Logo" className="logo" />
      <div className="navBarMenu">
        {navItems.map((item, index) => (
          <Link
            key={index}
            className="navBarMenuListItem"
            activeClass="active"
            to={item.to}
            spy={true}
            smooth={true}
            offset={-100}
            duration={1000}
          >
            {item.text}
          </Link>
        ))}
      </div>
      <img src={menu} alt="Menu" className="mobMenu" onClick={toggleMenu} />
      <div className="navMenu" style={{ display: show ? "flex" : "none" }}>
        {navItems.map((item, index) => (
          <Link
            key={index}
            className="listItem"
            activeClass="active"
            to={item.to}
            spy={true}
            smooth={true}
            offset={-100}
            duration={1000}
            onClick={toggleMenu}
          >
            {item.text}
          </Link>
        ))}
      </div>
    </nav>
  );
};

export default NavBar;
